import React, { useState } from 'react';
import './App.css';


const App = () => {
  const [filmy, setFilmy]=useState({
    zdj: [],
    tytul: ["akademia pana kleksa","aquaman i zaginione królestwo","ferrari","fuks 2",""],
    gatunek: ["familijny","science-fiction/akcja","biograficzny/dramat","komedia/sensacyjny","komedia/dramat"],
    premiera: ["PREMIERA","PREMIERA","PREMIERA","PREMIERA","PREMIERA"],
    czas1: ["18:00","19:00","15:00","17:00","18:30"],
    czas2: ["21:00","20:00","22:00","20:30","23:00"]
  });

  return (
    <div>
    <div>
  {Object.values(filmy.tytul).map((_, index) => (
    <div className='film' id={index} key={index}>
      <img id="zdjecie" src="/{filmy.zdj[index]}" alt={filmy.tytul[index]} />
      <h3 id="tytul">{filmy.tytul[index]}</h3>
      <p id='gatunek'>{filmy.gatunek[index]}</p>
      <p id='premiera'>{filmy.premiera[index]}</p>
      <p id='czas'>{filmy.czas1[index]}</p>
      <p id='czas'>{filmy.czas2[index]}</p>
    </div>
  ))}
</div>

      
        
    </div>
  );
};

export default App;
